# Translating RNA into Protein
rna_codon_table = "
UUU F      CUU L      AUU I      GUU V
UUC F      CUC L      AUC I      GUC V
UUA L      CUA L      AUA I      GUA V
UUG L      CUG L      AUG M      GUG V
UCU S      CCU P      ACU T      GCU A
UCC S      CCC P      ACC T      GCC A
UCA S      CCA P      ACA T      GCA A
UCG S      CCG P      ACG T      GCG A
UAU Y      CAU H      AAU N      GAU D
UAC Y      CAC H      AAC N      GAC D
UAA Stop   CAA Q      AAA K      GAA E
UAG Stop   CAG Q      AAG K      GAG E
UGU C      CGU R      AGU S      GGU G
UGC C      CGC R      AGC S      GGC G
UGA Stop   CGA R      AGA R      GGA G
UGG W      CGG R      AGG R      GGG G "

#mRNA = "AUGGCCAUGGCGCCCAGAACUGAGAUCAAUAGUACCCGUAUUAACGGGUGA"
mRNA = ""
function codonTable()
    codon_table = replace(rna_codon_table, "\n" => "   ")
    codonTable = split(codon_table, "   ")
    dCodon = Dict()
    for i in eachindex(codonTable)
        if codonTable[i] != ""
            dCodon[split(codonTable[i])[1]] = split(codonTable[i])[2]
        end
    end
    return dCodon
end


function translatingRNA(mRNA)
    dCodon = codonTable()
    h = 1
    protein = ""
    stop = false
    while (h < length(mRNA)) && (!stop)
        if dCodon[mRNA[h: h + 2]] != "Stop"
            protein = protein * dCodon[mRNA[h: h + 2]]
        else
            stop = true
        end
        h = h + 3
    end
    println(protein)
end

translatingRNA(mRNA)